from PIL import Image, ImageDraw

def generate_binary_image(binary_string, base_size=500, resolution_multiplier=2, cell_color_1="black", cell_color_0="white", border_color="gray"):
    """
    Generates a binary visualization image with adjustable resolution.

    :param binary_string: The binary string to visualize.
    :param base_size: The base size for the image (default 500px).
    :param resolution_multiplier: Multiplier for increasing the resolution.
    :param cell_color_1: Color for cells representing '1'.
    :param cell_color_0: Color for cells representing '0'.
    :param border_color: Color for cell borders.
    :return: The generated image.
    """
    # Clean the binary string to include only '0' and '1'
    binary_string = ''.join(filter(lambda x: x in '01', binary_string))
    
    if not binary_string:
        raise ValueError("The binary string is empty or contains no valid binary characters.")

    binary_list = list(map(int, binary_string))

    # Determine grid size
    total_length = len(binary_list)
    grid_size = int(total_length ** 0.5) + (1 if total_length ** 0.5 % 1 else 0)

    # Adjust resolution
    image_size = base_size * resolution_multiplier
    cell_size = image_size // grid_size

    # Create a new image with a white background
    image = Image.new("RGB", (grid_size * cell_size, grid_size * cell_size), "white")
    draw = ImageDraw.Draw(image)

    # Draw cells
    for index, bit in enumerate(binary_list):
        row = index // grid_size
        col = index % grid_size
        x0, y0 = col * cell_size, row * cell_size
        x1, y1 = x0 + cell_size, y0 + cell_size

        # Fill color based on binary value
        fill_color = cell_color_1 if bit else cell_color_0
        draw.rectangle([x0, y0, x1, y1], fill=fill_color, outline=border_color)

    return image

